   }
 }

 function mandarBackUpMail(){
   var nombre = mes + "_" + ano;
   var directorio = "/home/gaston/BackUp-Node-Red/";
   msg = {topic : "SELECT * FROM crepuscular ORDER BY hora Asc"};
   node.send([null, null, msg, null]);
   msg = {topic : "SELECT \"hora\", \"valor\" UNION  SELECT \`hora\`, \`valor\` INTO OUTFILE \'" + directorio + nombre + ".csv\' FIELDS TERMINATED BY \';\' OPTIONALLY ENCLOSED BY '\"' LINES TERMINATED BY '\r\n' FROM \`crepuscular\`"};
   node.send([null, null, msg, null]);
   msg = {topic : "DELETE FROM \`crepuscular\` WHERE 1"};
   node.send([null, null, msg, null]);

   msg.topic = "BackUp crepuscular";
   msg.payload = "BackUp crepuscular";
   msg.attachments = [{
       filename: nombre + '.csv',
       path: directorio + nombre + '.csv',
       content: msg.payload
   }];
   node.send([null, null, null, msg]);
   context.avisoMail = 1;
 }

