function enviarMQTT(caso){
var dato;
  if (caso == "bano"){
  dato = global.get("estLuzBano") + "#" + global.get("pwmLuzBano") + "#" + global.get("modoDucha") + "#" + global.get("estDucha") + "#" + global.get("pwmDucha");
