    msg = {topic: "ESP/Bano/Recibo", payload: dato};
  }

  if (caso == "patioLuz"){msg = {topic: "USBout", payload: "Noche" + global.get("pwmPatioLuz")};}


  if (caso == "living"){
    dato = global.get("estIsla") + ",";
    dato += global.get("potIsla") + "#";
    dato += global.get("estCielorrazo") + ",";
    dato += global.get("potCielorrazo") + "#";  
    dato += global.get("estIslaRGB") + ",";
    dato += global.get("IslaR") + ",";
    dato += global.get("IslaG") + ",";      
    dato += global.get("IslaB") + "#";

    dato += global.get("pwmMaxLed") + "#";
    dato += global.get("pwmMinLed") + "#";

    dato += global.get("minTeclas") + "#";
    dato += global.get("maxTeclas") + "#";
    dato += global.get("doblePuls");
    dato += "#" + global.get("Dia");

    msg = {topic: "ESP/living/Iluminacion/Recibo", payload: dato};
    node.send([msg, null]);
  }
  if (caso == "configGenericas"){
    dato = global.get("minTeclas") + "#";
  dato += global.get("maxTeclas") + "#";
  dato += global.get("delayBuzzer");
  msg = {topic: "configTeclas", payload: dato};
  }

node.send([msg, null]);
}

