function enviarSalamandra(){
  enviarDato = 0;
  if (parseInt(global.get("tempDepRadiadores")) > 85){
    global.set("estadoTurbo", 0);
    global.set("aire1", 0);
    global.set("aire2", 0);
    global.set("aire3", 0);
    global.set("aire4", 0);
    msg = {topic: "notifyBlynk", payload: "Error exeso temperatura salamandra"}; node.send([msg, null, null, null]);
    msg = {topic: "terminalBlynk", payload: global.get("hsCpu") + " -- Error exeso temperatura salamandra\n"}; node.send([msg, null, null, null]);
  }

    if (parseInt(global.get("salamandraPausa")) === 1){
      flow.set("estadoTurboF", 0);//salamandraPausa == 0 esta en marcha
      flow.set("aire1F", 0);
      flow.set("aire2F", 40);
      flow.set("aire3F", 0);
      flow.set("aire4F", 0);
      flow.set("aireChimeneaF", 100); 
    }
    if (parseInt(global.get("salamandraPausa")) === 2){
      flow.set("estadoTurboF", global.get("estadoTurbo"));//salamandraPausa == 0 esta en marcha
      flow.set("aire1F", global.get("aire1"));
      flow.set("aire2F", global.get("aire2"));
      flow.set("aire3F", global.get("aire3"));
      flow.set("aire4F", global.get("aire4"));
      flow.set("aireChimeneaF", global.get("aireChimenea"));  
    } 

    if (parseInt(global.get("estadoSalamandra")) === 0){
      flow.set("estadoTurboF", 0);//salamandraPausa == 0 esta en marcha
      flow.set("aire1F", 0);
      flow.set("aire2F", 0);
      flow.set("aire3F", 0);
      flow.set("aire4F", 0);
      flow.set("aireChimeneaF", 0);       
    }

  //  msg = {topic: "salamandra/backUp", payload: "estado" };
  //  node.send([msg, null, null, null]);
    // dato = global.get("salamandraAuto") + "#";
  dato = global.get("salamandraBbaCirc") + "#";
  dato += global.get("salamandraBbaRad") + "#";
  dato += global.get("salamandraBbaTermo") + "#";
  dato += flow.get("estadoTurboF") + "#";//salamandraPausa == 0 esta en marcha
  dato += flow.get("aire1F") + "#";
  dato += flow.get("aire2F") + "#";
  dato += flow.get("aire3F") + "#";
  dato += flow.get("aire4F") + "#";
  dato += flow.get("aireChimeneaF");    

  // dato += global.get("estadoTurbo") + "#";


  if (dato.includes("undefined")){
    msg = {topic: "salamandra/backUp/recovery", payload: 1}; 
    node.send([msg, null]); 
    return;
  }
  msg = {topic: "salamandra/backUp", payload: "estado" };
  node.send([msg, null, null, null]);
  msg = {estado: "node", topic: "salamandra/Recibo", payload: "estado" + dato}; 
  if (topicInicial == "salamandra/NodeRed")msg = {estado: "node", topic: "salamandra/Recibo", payload: "estado" + dato};
  node.send([msg, null, null, null]);
}




