function enviarMQTT(caso){
var dato;
  if (caso == "bano"){
  dato = global.get("estLuzBano") + "#" + global.get("pwmLuzBano") + "#" + global.get("modoDucha") + "#" + global.get("estDucha") + "#" + global.get("pwmDucha");
  dato += "#" + global.get("estLuzEspejo") + "#" + global.get("estExtractorBano") + "#" + global.get("timerExtractorBano") + "#" + global.get("Dia");
    msg = {topic: "ESP/Bano/Recibo", payload: dato};
  }

  if (caso == "escaleras"){
      if (parseInt(global.get("estReflectorPergola")) === 1){
         dato = "estado" + global.get("pwmReflectorPergola");// + "#0#0#0#0#0#1";
      }
      if (parseInt(global.get("estReflectorPergola")) === 0){
         dato = "estado0";//#0#0#0#0#0#1";
      }
      dato = dato + "#";
      if (parseInt(global.get("estEscaleras")) === 1){
         // if (topicOriginal == "crepMQTT"){dato = dato + "7";} else{dato = dato + global.get("pwmReflectorPergola");}
         dato = dato + global.get("pwmEscaleras");
         // dato = dato + global.get("pwmReflectorPergola");// + "#0#0#0#0#0#1";
      }
      if (parseInt(global.get("estEscaleras")) === 0){
         dato = dato + "0";//;//#0#0#0#0#0#1";
      }    
