    function enviarMQTT(caso){
    var dato;
      if (caso == "bano"){
          dato = "estado" + global.get("estLuzBano") + "#" + global.get("pwmLuzBano");
          dato += "#" + global.get("estLuzEspejo") + "#" + global.get("pwmLuzBano");
          dato += "#1" ;//+ global.get("estExtractorBano");
          dato += "#" + global.get("incrementoBa√±o");
          dato += "#" + global.get("minTeclas");
          dato += "#" + global.get("maxTeclas");
          dato += "#" + global.get("delayBuzzer");          
        msg = {topic: "ESP/Bano/Recibo", payload: dato};
      }

    if (caso == "PiletaRedonda"){

        dato = "estado" + global.get("estLucesPiletaRedonda") + "#" + global.get("modoLucesPiletaRedonda") + "#";
        dato = dato + global.get("PiletaRedondaR") + "#";
        dato = dato + global.get("PiletaRedondaG") + "#";
        dato = dato + global.get("PiletaRedondaB");
        msg = {topic: "ESP/Pileta/Redonda/BackUp", payload: dato}; node.send([msg, null]);

        if (parseInt(global.get("estLucesPiletaRedonda")) === 1){
           dato = "estado" + global.get("modoLucesPiletaRedonda") + "#";
           dato = dato + global.get("PiletaRedondaR") + "#";
           dato = dato + global.get("PiletaRedondaG") + "#";
           dato = dato + global.get("PiletaRedondaB") + "#";}
        else{  
            dato = "estado0#0#0#0#"; 
           }
        dato = dato + "1#10#1#255";
